package main

import (
	"encoding/hex"
	"fmt"

	"github.com/cortexproject/cortex/pkg/chunk"
)

func main() {
	chunkStr := ""
	chunkBytes, err := hex.DecodeString(chunkStr)
	if err != nil {
		panic(err)
	}

	c := chunk.Chunk{}
	decodeContext := chunk.NewDecodeContext()
	err = c.Decode(decodeContext, chunkBytes)
	if err != nil {
		panic(err)
	}

	fmt.Println(fmt.Sprintf("%+v", c))
}
